var Weapon = require('game/weapon');

var Player = function Player (id, body) {
    this.id = id;
    this.body = body;
    this.health = 100;
    this.max_health = 100;
    this.weapon = new Weapon(this);
    this.name = '';
    this.team = '';
};

Player.prototype = {
    tick: function(elapsedTime) {
        this.body.tick(elapsedTime);
        this.weapon.tick(elapsedTime);
    }
};

module.exports = Player;