var red   	= '\u001b[31m';
var green	= '\u001b[32m';
var yellow	= '\u001b[33m';
var blue  	= '\u001b[34m';
var magenta	= '\u001b[34m';
var cyan	= '\u001b[36m';
var reset 	= '\u001b[0m';


exports.red   	= red;
exports.green	= green;
exports.yellow	= yellow;
exports.blue  	= blue;
exports.magenta	= magenta;
exports.cyan	= cyan;
exports.reset 	= reset;

exports.log = function(log, color){
	if(typeof(color) != 'undefined') {
		console.log(color+log+reset);
	}
	else {
		console.log(log);
	}
};